  getRAPs = () => {
    return this.http.get(this.baseUrl) ;
  }
  getRAP = (id:number)=>{
    return this.http.get(`${this.baseUrl}/${id}`);
  }
  postRAP = (album:any)=> {
    return this.http.post(this.baseUrl, album);

  }


  // getBOSSANOVA = () => {
