@Component({
  selector: 'app-nav',
  templateUrl: './nav.component.html',
  styleUrls: ['./nav.component.css']
})
export class NavComponent {

}
<nav>
    <ul>
        <li><button><a routerLink="/"> <img src="../../.. " alt="iconoMenu"> Home</a></button></li> 
        <li><button><a routerLink="/albums">Albums</a></button></li>
        <li><button><a routerLink="/formularios">Formularios</a></button></li>
    </ul>
</nav>
