import { Component } from '@angular/core';

@Component({
  selector: 'app-detalle-artista',
  templateUrl: './detalle-artista.component.html',
  styleUrls: ['./detalle-artista.component.css']
})
export class DetalleArtistaComponent {

}
<h1>Descripcion de cada artista</h1>

<   
    
        <div class="artista">
        <h1>{{album.nombre}}</h1>
        <div>
            <p>Edad: {{album.edad}}</p>
            <p>Origen: {{album.origen}}</p>
            <img class="fotos" src="{{album.src}}" alt="{{album.nombre}}">
            <button routerLink="/albums/{{album.nombre}}">Detalles</button>
        </div>
        </div>
