import { Component } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { ApiService } from 'src/app/services/api.service';

@Component({
  selector: 'app-formularios',
  templateUrl: './formularios.component.html',
  styleUrls: ['./formularios.component.css']
})
export class FormulariosComponent {
albumsForm!: FormGroup;  
constructor(private servicio:ApiService,private formBuilder:FormBuilder){}
public nuevoAlbum = this.servicio.albumInfo;
ngOnIt():void{
  this.albumsForm =  this.formBuilder.group({
    id: [
      this.nuevoAlbum.id, [Validators.required, Validators.maxLength(2)]
    ],
    nombre: [
      this.nuevoAlbum.nombre, [Validators.required, Validators.minLength(4)]
    ],
    edad: [
      this.nuevoAlbum.edad
    ],
    origen: [
      this.nuevoAlbum.origen
    ],
    src: [
      this.nuevoAlbum.src
    ],
    alt: [
      this.nuevoAlbum.alt
    ]
  })
}
onSubmit = () => { 
this.servicio.postRAP(this.nuevoAlbum).subscribe();

}

}
