</div> -->

<form [formGroup]="albumsForm" >
    <input class="teste" formControlName="id" placeholder="id">
    <input class="teste" formControlName="nombre" placeholder="nombre">
    <input class="teste" formControlName="edad" placeholder="edad">
    <input class="teste" formControlName="origen" placeholder="origen">
    <input class="teste" formControlName="src" placeholder="imagen">
    <input class="teste" formControlName="alt" placeholder="cantante" />
</form>
